import React from "react";
import { useParams } from "react-router-dom";
import Header from "./components/header";

export default function PassParam(props) {

    const { city } = useParams();

    return (<Forecast city={`${city.toLowerCase()}`} />)

}

class Forecast extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            info: {}
        }


    }
    getWeather(){
        const weather = fetch(`http://api.weatherapi.com/v1/forecast.json?key=${process.env.REACT_APP_FORECAST_API_KEY}&q=${this.props.city}&days=1&aqi=no&alerts=no
        `,{method:'GET'}).then(res => res.json());
        console.log(weather);
        this.setState({info: weather})

    }
    render() {
        var data = this.state.info;

        return (
            <>
                <Header />
                <h1>
                    {console.log(data.location)}
                </h1>
            </>
        )
    }
}
