import React from "react";
import './forecastDays.css';
import data from '../images/images.json';

export default class ForecastDays extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            backgroundUrl: ""
        }
    }
    dateFormatter(date) {
        let day, month, year;
        const splittedDate = date.split('-');
        day = splittedDate[2];
        month = splittedDate[1];
        year = splittedDate[0];

        return `${day}.${month}.${year}`;
    }
    componentDidMount(){
        const index = Math.floor(Math.random()*4);
        this.setState({backgroundUrl: data.squares[index].url})
    }
    render() {
        const photo = require("../"+this.state.backgroundUrl)
        return (
            <>
                <div id="days">
                    {this.props.forecastForDays.map(item =>
                        <div style={{backgroundImage: `url(${photo})`}} className="day">
                            <div className="dayText">
                                <h1 className="dayDate">{this.dateFormatter(item.date)}</h1>
                                <div className="dayInfo">
                                    <p>{item.day.maxtemp_c}</p>
                                    <div></div>
                                    <p>{item.day.condition.text}</p>
                                </div>
                            </div>
                        </div>)}
                </div>
            </>
        )
    }

}
