import React from "react";
import { useParams } from "react-router-dom";
import Header from "./components/header";

export default function PassParam(props) {

    const { city } = useParams();

    return (<Forecast city={`${city.toLowerCase()}`} />)

}

class Forecast extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            info: []
        }

    }
    async getWeather() {
        const weather = await fetch(`https://api.weatherapi.com/v1/forecast.json?key=${process.env.REACT_APP_FORECAST}&q=${this.props.city}&days=1&aqi=no&alerts=no
        `)

        this.setState({ info: [...weather] })

    }

    componentDidMount() {
        this.getWeather();
    }

    render() {
        return (
            <>
                <Header />
            </>
        )
    }
}
