import React from "react";
import './chatScreen.css';
import { FiSend } from 'react-icons/fi';
import { useParams, useSearchParams } from "react-router-dom";

export default function ChatScreen(){
    const param = new useSearchParams();

    return <ChatScreenInside roomName={useParams}/>
}

class ChatScreenInside extends React.Component {
    render() {
        return (
            <>
                <div id="messages">

                </div>
                <div id="controller">
                    <input type="text" placeholder="Enter your message..." id="message" />
                    <button id="send"><FiSend/></button>
                </div>
            </>
        )
    }
}
