import React from "react";
import { Link } from "react-router-dom";
import './joinForm.css';

export default class JoinForm extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            userName: "",
            roomName: "",
        }
        this.nameInputRef = React.createRef();
    }
    componentDidMount(){
        this.nameInputRef.current.addEventListener('change',()=>{
            this.setState({userName: this.nameInputRef.current.value,})
        })
    }
    render() {
        return (
            <>
                <div id="roomForm">
                    <h1>Project-Chat</h1>
                    <input ref={this.nameInputRef} id="socketName" placeholder="What is your name darling ?" type="text" />
                    <input id="socketRoom" placeholder="And please tell me where you want to chat ?" type="text" />
                    <Link id="enterRoom" to={`/room/`}>GO THERE!</Link>
                </div>
            </>
        )
    }
}
