import React from "react";
import './chatScreen.css';
import { FiSend } from 'react-icons/fi';
import socket from "../socket";


export default class ChatScreen extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            messages: []
        }
        this.messageScreenRef = React.createRef();
        this.buttonRef = React.createRef();
    }
    displayMessage(message){
        const div = document.createElement('div');
        const innerDiv = document.createElement('p');
        innerDiv.className = `messageText`;
        innerDiv.innerHTML = `${message.content}`;
        div.appendChild(innerDiv);
        div.className = `message ${message.sender}`;
        this.messageScreenRef.current.appendChild(div);


    }
    componentDidMount(){
        this.buttonRef.current.addEventListener('click',this.displayMessage({sender: socket.userName,content:"askjdlasjdklasjdl"}))
    }
    render() {
        return (
            <>
                <div ref={this.messageScreenRef} id="messages">

                </div>
                <div id="controller">
                    <input type="text" placeholder="Enter your message..." id="message" />
                    <button ref={this.buttonRef} id="send"><FiSend/></button>
                </div>
            </>
        )
    }
}
